Example_SearchLoop.Code:
FOR #tempIndex := 0 TO 999 DO
   IF "MaximalkonturDB".Progr[#tempIndex].nProgrNumber = #TargetID THEN
       #FoundIndex := #tempIndex;
       EXIT;
   END_IF;
END_FOR;

Example_FreeSlotSearch.Code:
FOR #tempIndex := 1 TO 999 DO
   IF "MaximalkonturDB".Progr[#tempIndex].nProgrNumber = 0 THEN
       #FreeIndex := #tempIndex;
       EXIT;
   END_IF;
END_FOR;
